blog_post.graphql_mutation:
mutation {
  createContentType(
    name: "Blog Post"
    slug: "blog-post"
    description: "Blog posts for your website"
    schema: "{\"type\":\"object\",\"properties\":{\"title\":{\"type\":\"string\"},\"slug\":{\"type\":\"string\"},\"body\":{\"type\":\"string\"},\"excerpt\":{\"type\":\"string\"},\"featured_image\":{\"type\":\"string\"},\"author\":{\"type\":\"string\"},\"tags\":{\"type\":\"array\",\"items\":{\"type\":\"string\"}}}}"
  ) {
    id
    name
    slug
  }
}

product.graphql_mutation:
mutation {
  createContentType(
    name: "Product"
    slug: "product"
    description: "E-commerce products"
    schema: "{\"type\":\"object\",\"properties\":{\"name\":{\"type\":\"string\"},\"sku\":{\"type\":\"string\"},\"description\":{\"type\":\"string\"},\"price\":{\"type\":\"number\"},\"currency\":{\"type\":\"string\"},\"images\":{\"type\":\"array\",\"items\":{\"type\":\"string\"}},\"inventory\":{\"type\":\"integer\"}}}"
  ) {
    id
    name
    slug
  }
}

page.graphql_mutation:
mutation {
  createContentType(
    name: "Page"
    slug: "page"
    description: "Static pages"
    schema: "{\"type\":\"object\",\"properties\":{\"title\":{\"type\":\"string\"},\"slug\":{\"type\":\"string\"},\"content\":{\"type\":\"string\"},\"meta_title\":{\"type\":\"string\"},\"meta_description\":{\"type\":\"string\"},\"layout\":{\"type\":\"string\"}}}"
  ) {
    id
    name
    slug
  }
}

author.graphql_mutation:
mutation {
  createContentType(
    name: "Author"
    slug: "author"
    description: "Content authors"
    schema: "{\"type\":\"object\",\"properties\":{\"name\":{\"type\":\"string\"},\"bio\":{\"type\":\"string\"},\"avatar\":{\"type\":\"string\"},\"email\":{\"type\":\"string\"},\"social_links\":{\"type\":\"object\",\"properties\":{\"twitter\":{\"type\":\"string\"},\"github\":{\"type\":\"string\"},\"linkedin\":{\"type\":\"string\"}}}}}"
  ) {
    id
    name
    slug
  }
}
